settings.custom_css:
selector .elementor-column-wrap:hover{
    border-color: var(--primary);
}
selector .elementor-column-wrap:hover .elementor-icon{
    transform: scale(1.1);
}

selector .elementor-column:hover .elementor-widget-image-box{
    transform: translateY(-20px);
}
selector .elementor-widget-image-box{
    transition: all 0.4s ease; 
}

selector .icon{
    display: none;
}
selector .slick-slide{
    border: none !important;
}

selector .elementor-inner-section:hover .elementor-widget-spacer .elementor-widget-container{
    border-color: var(--primary);
    transition: all 0.3s ease
}
selector .elementor-inner-section:hover .effect-hover .elementor-widget-container{
    background-color:var(--primary);
    transition: all 0.3s ease
}
selector .elementor-inner-section:hover .elementor-heading-title{
    color: #fff;
    transition: all 0.3s ease
}

selector .elementor-widget-icon-box .elementor-icon{
    border:solid 2px var(--primary);
}
selector .elementor-widget-icon-box:hover .elementor-icon{
    transform: scale(1.15);
    color: var(--primary);
    background-color: transparent;

}

selector .elementor-icon-box-animation-framed-yes .elementor-icon{
